def top_3_accuracy(y_true, y_pred):
    return top_k_categorical_accuracy(y_true, y_pred, k=3)

def top_5_accuracy(y_true, y_pred):
    return top_k_categorical_accuracy(y_true, y_pred, k=5)

# def preds2catids(predictions):
#     return pd.DataFrame(np.argsort(-predictions, axis=1)[:, :5], columns=['a', 'b', 'c', 'd', 'e'])

def preds2catids(predictions):
    top5_indices = np.argsort(-predictions, axis=1)[:, :5].astype(int)
    top5_probs = np.take_along_axis(predictions, top5_indices, axis=1)

    df = pd.DataFrame(top5_indices, columns=['a', 'b', 'c', 'd', 'e'])
    df['probs'] = top5_probs.tolist()
    
    return df

def draw_cv2(raw_strokes, size=256, lw=6, time_color=True):
    BASE_SIZE = 256
    img = np.zeros((BASE_SIZE, BASE_SIZE), np.uint8)
    for t, stroke in enumerate(raw_strokes):
        for i in range(len(stroke[0]) - 1):
            color = 255 - min(t, 10) * 13 if time_color else 255
            _ = cv2.line(img, (stroke[0][i], stroke[1][i]),
                         (stroke[0][i + 1], stroke[1][i + 1]), color, lw)
    if size != BASE_SIZE:
        return cv2.resize(img, (size, size))
    else:
        return img

def df_to_image_array_xd(df, size, lw=6, time_color=True):
    # df['drawing'] = df['drawing'].apply(json.loads)
    x = np.zeros((len(df), size, size, 1))
    for i, raw_strokes in enumerate(df.drawing.values):
        x[i, :, :, 0] = draw_cv2(raw_strokes, size=size, lw=lw, time_color=time_color)
    x = preprocess_input(x).astype(np.float32)
    return x

NCATS = 340
size = 64
model = MobileNet(input_shape=(size, size, 1), alpha=1., weights=None, classes=NCATS)
model.load_weights('model.h5')
model.compile(optimizer=Adam(learning_rate=0.002), loss='categorical_crossentropy',
              metrics=[categorical_crossentropy, categorical_accuracy, top_3_accuracy])

print("## model loaded")

df = pd.read_json('output.ndjson', lines=True)
x_test = df_to_image_array_xd(df, size)
prediction = model.predict(x_test)
top3 = preds2catids(prediction)
id2cat = {k: cat.replace(' ', '_') for k, cat in enumerate(cats)}
top3cats = top3.replace(id2cat)

print("## prediction")
print(top3cats)
